import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

const store = new Vuex.Store({
  state: {
    user: null,
    loginFlag: false,
  },
  mutations: {
    setUser(state, user) {
      state.user = user;
    },
    setLoginFlag(state, loginFlag) {
      state.loginFlag = loginFlag;
    },
  },
});

export default store;
