import axios from 'axios';
export default {
  name: 'AlbumView',
  components: {},
  props: ['id', 'name'],
  data() {
    return {
      tracks: null
    };
  },
  methods: {
    fetchTracks() {
      axios.get(`http://127.0.0.1:3000/getTracksByOrder/${this.id}`).then(response => {
        this.tracks = response.data;
        console.log(this.tracks);
      }).catch(error => console.error(error));
    }
  },
  mounted() {
    this.fetchTracks();
  }
};
